mapper.select:
SELECT REPORT_NUMBER,KEYWORD_NUMBER,USER_NUMBER,REPORT_DATE, REPORT_IMG_NUMBER,REPORT_IMG_NAME, REPORT_IMG_PATH, SPOT_NUMBER,SPOT_LAT,SPOT_ING,SPOT_NAME, KEYWORD_NAME FROM(SELECT REPORT.REPORT_NUMBER, USER_NUMBER, REPORT.KEYWORD_NUMBER,TO_CHAR(REPORT_DATE,'YYYY/MM/DD') REPORT_DATE, RI.REPORT_IMG_NUMBER, REPORT_IMG_NAME, REPORT_IMG_PATH, CRS.SPOT_NUMBER,SPOT_LAT, SPOT_ING, SPOT_NAME, RK.KEYWORD_NAME FROM CLEAN_REPORT REPORT LEFT JOIN REPORT_IMG RI on REPORT.REPORT_NUMBER = RI.REPORT_NUMBER LEFT JOIN CLEAN_REPORT_SPOT CRS on REPORT.REPORT_NUMBER = CRS.REPORT_NUMBER INNER JOIN REPORT_KEYWORD RK on REPORT.KEYWORD_NUMBER = RK.KEYWORD_NUMBER
AND REPORT.REPORT_NUMBER =#{reportNumber}
ORDER BY REPORT.REPORT_NUMBER DESC )
